sap.ui.define([
    "sap/ui/core/mvc/Controller"
],
    function (Controller) {
        "use strict";

        return Controller.extend("com.tatatech.modelfilter.controller.View1", {
            onInit: function () {

            },
            onLiveChange: function(oEvent) {
                //This code was generated by the layout editor.
                //var oTbl = this.getView().byId(this.getView().createId("__table0"));
            
    
                // sap.ui,model.Binding
                
                var sVal = oEvent.getParameter("newValue");  //Input value
                var _oCityFilter = new sap.ui.model.Filter("City", sap.ui.model.FilterOperator.Contains, sVal);
                
                var oFilter = [];
                oFilter.push(_oCityFilter);
                
                 var oItems = this.getView().byId("__table0").getBinding("items");
                  oItems.filter(oFilter);
    
            },
            /*
             *
             */
            onSortCntry: function(oEvent) {
                //This code was generated by the layout editor. 
    //			var oTbl = this.getView().byId(this.getView().createId("__table0"));
    
    
    //			var oSorter = new sap.ui.model.Sorter("Country", true, this._fnVGroup);
    var oSorter = new sap.ui.model.Sorter("Country", true);
                this.getView().byId("__table0").getBinding("items").sort(oSorter);
    
            }
    
        });
    });
